import { useState } from 'react'

export default function Calculator () {

        const [display, setDisplay] = useState(0)

        let currentCalc = '';

        const handleClick = (event) => {
            event.preventDefault();
            setDisplay(event.target.value);
            currentCalc = currentCalc + event.target.value;
            console.log(currentCalc);
        }

        const handleSubmit = () => {
            setDisplay(eval(currentCalc));
        }

        const resetCalc = () =>{
            setDisplay(0);
            currentCalc="";
        }

    return (
        <div id="container">
            <section id="result"><h1>{display}</h1></section>
            <section id="buttons-container">
                <button className="calc-button">AC</button>
                <button className="calc-button">Back</button>
                <button className="calc-button">%</button>
                <button className="calc-button">/</button>
                <button className="calc-button">7</button>
                <button className="calc-button">8</button>
                <button className="calc-button">9</button>
                <button className="calc-button">*</button>
                <button className="calc-button">4</button>
                <button className="calc-button">5</button>
                <button className="calc-button">6</button>
                <button className="calc-button">1</button>
                <button className="calc-button">2</button>
                <button className="calc-button">3</button>
                <button className="calc-button">+</button>
                <button className="calc-button">0</button>
                <button className="calc-button">.</button>
                <button className="calc-button">=</button>
            </section>
        </div>
    )

}
